sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        if (inputs['delete_after'].equals('yes')) {
            var srcRecord = inputs['grAttachment'];
            var gr = new GlideRecord(srcRecord.table_name);
            if (gr.get(srcRecord.table_sys_id)) {
                gs.info("##### Deletando registro...")
                gr.deleteRecord();
            }
        }
        gs.info("##### Cleanup success")
    } catch (ignore) {
        gs.info("##### Cleanup failed: " + ignore)
    }
})(inputs, outputs);

