sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var x = new BinaryXmlParser("frango", {});
        gs.info("##### " + x.buffer)
        var srcRecord = inputs['grAttachment'];
        var srcFilename = srcRecord.file_name;
        if (srcFilename.endsWith('.apk')) {
            var newFilename = srcFilename.replace('.apk', '.zip');
            var gsa = new GlideSysAttachment();
            var srcContentStream = gsa.getContentStream(srcRecord.sys_id);
            var dstRecord = new GlideRecord('x_snc_safety_apk_file');
            dstRecord.initialize();
            dstRecord.insert();
            outputs['lookup_sysid'] = gsa.writeContentStream(
                dstRecord,
                newFilename,
                'application/zip',
                srcContentStream);
            outputs['delete_after'] = 'yes'
        } else {
            outputs['lookup_sysid'] = srcRecord.sys_id;
            outputs['delete_after'] = 'no'
        }
        gs.info('##### Fix APK attachment success')
    } catch (ignore) {
        gs.info('##### Fix APK attachment failed:' + ignore)
    }
})(inputs, outputs);

