sys_variable_value.value:
(function execute(inputs, outputs) {
    outputs.status = 'Success';
    gs.error(`##### Vai tentar ${inputs.SysId}`)
    gs.error(`##### 1 ${outputs}`);
    gs.error(`##### 2 ${outputs.status}`);
    var attachmentGR = new GlideRecord('sys_attachment');
    attachmentGR.addQuery('table_name', inputs.SysId);
    attachmentGR.query();
    gs.log("##### Vai tentar");
    if (attachmentGR.next()) {
        gs.log("##### Entrou aqui");
        gs.log("##### "+ attachmentGR.file_name);
    }
    return outputs;
})(inputs, outputs);

