var Uint8Array = function(input) {
    var i;
    if (typeof input === 'number') {
        this.length = ~~input;
        for (i = 0; i < input; i++) {
            this[i] = 0;
        }
    } else {
        this.length = ~~input.length;
        for (i = 0; i < input.length; i++) {
            this[i] = input[i];
        }
    }
};

Uint8Array.prototype = Array.prototype;

Uint8Array.prototype.readUInt16LE = function(offset = 0) {
    return (this[offset + 1] << 8) | this[offset];
};

Uint8Array.prototype.readInt32LE = function(offset = 0) {
    return (this[offset + 3] << 24) |
        (this[offset + 2] << 16) |
        (this[offset + 1] << 8) |
        this[offset];
};

Uint8Array.prototype.readUInt32LE = function(offset = 0) {
    return (this[offset + 3] << 24 >>> 0) +
        (this[offset + 2] << 16) +
        (this[offset + 1] << 8) +
        this[offset];
};

Uint8Array.prototype.toString = function(encoding = 'utf8', start = 0, end = this.length) {
    var string = '';
    var i;
    if (encoding === 'ucs2') {
        for (i = start; i < end; i += 2) {
            string += String.fromCharCode(this.readUInt16LE(i));
        }
    } else if (encoding === 'utf8') {
        for (i = start; i < end; i++) {
            string += String.fromCharCode(this[i]);
        }
    } else {
        throw new Error("Unexpected encoding: " + encoding);
    }
    return string;
};

Uint8Array.prototype.type = 'Uint8Array';
